package com.MyProject;

public class pattern21 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<=10;i++) {
			for(int j=0;j<=10;j++) {
				if(i%2==0) {
					System.out.print("1");
				}
				else 
					System.out.print("0");
			}
			System.out.println();
		}

	}

}
