package com.october15;

public class buildMethodString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      String str="SachinRameshTendulkar";
      // convert into upper case
      System.out.println(str.toUpperCase());
      // convert into lower case
      System.out.println(str.toLowerCase());
      //find the total lenght of string
      System.out.println(str.length());
      //find the character  specified of the index
      System.out.println(str.charAt(5));
      //System.out.println(str.charAt(50));// stringindexoutof boundexception
      
	}

}
